import React from 'react'


const FormSignup = () => {
  return (
    // <div class="container max-w-5xl mx-auto m-8 ">
    //   <div className="flex min-h-full items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
    //     <div className="w-full max-w-md space-y-8">
    //       <div>
    //         <h2 className="mt-6 text-center text-3xl font-bold tracking-tight text-gray-900">
    //           Sign in to your account
    //         </h2>

    //         <button
    //           type="submit"
    //           className="group relative flex w-full justify-center rounded-md border border-transparent text-sm text-white focus:outline-none transition duration-300 px-7 py-3  bg-[#5E9387] text-center marker:sm:w-auto font-bold hover:shadow-md hover:bg-gray-700
    //           "
    //         >
    //           Sign in
    //         </button>
    //       </div>
    //     </div>
    //   </div>
    // </div>

    <div className="flex flex-col items-center justify-center min-h-screen py-2 bg-gray-100 mb-5">
      {/* <Head>
        <title>CarbonO</title>
      </Head> */}

      <main className="flex flex-col items-center justify-center w-full flex-1 px-20 text-center">
        <div className="bg-white rounded-2xl shadow-2xl flex w-2/3 max-w-4xl">
          <div className="w-35 p-5">
            <div>
              <a
                class="toggleColour text-[#5E9387] my-4 leading-tight no-underline hover:no-underline font-bold text-xl lg:text-4xl"
                href="#"
              >
                CarbonO
              </a>
            </div>
            <div className="py-10">
              <h2 className="text-2xl font-bold text-gray-700 mb-2">
                Sign in to your account
              </h2>
              <div className="border-2 w-10 border-gray-700 bg-gray-700 inline-block mb-2"></div>
            </div>

            <div className="flex flex-col items-center">
              <div className="bg-gray-100 w-64 p-2 flex items-center ">
                <input
                  type="email"
                  name="email"
                  placeholder="johndoe@gmail.com"
                  className="bg-gray-100 outline-none"
                />
              </div>
            </div>
          </div>
          {/* Sign In Section */}
          <div className="w-2/5 bg-[#5E9387]  text-white rounded-tr-2xl rounded-br-2xl py-36 px-12">
            <h2 className="text-3xl font-bold mb-2">Hello there!</h2>
            <div className="border-2 w-10 border-white inline-block mb-2"></div>
            <p className="mb-10">Start your journey with us today</p>
            <a
              href=""
              className="roup relative flex w-full justify-center rounded-md border border-transparent text-sm text-gray-700 focus:outline-none transition duration-300 px-7 py-3  bg-white text-center marker:sm:w-auto font-bold hover:shadow-md hover:bg-gray-700 hover:text-white"
            >
              Sign Up
            </a>
          </div>

          {/* Sign In Section */}
        </div>
      </main>
    </div>
  );
}

export default FormSignup;
