import React, {useEffect, useState, useRef} from 'react'
import StepperControl from './StepperControl'

const Stepper = ({steps, currentStep}) => {

  // useEffect to check for change in step
  useEffect(() => {
    // create object
    const stepsState = steps.map((step, index) =>
      Object.assign(
        {},
        {
          description: step,
          completed: false,
          highlighted: index === 0 ? true : false,
          selected: index === 0 ? true : false,
        }
      )
    );

  }, []);

  const displaySteps = (
    <div className = "w-full flex items-center">
      <div className="relative flex flex-col items-center text-teal-600">
        <div className="rounded-full transition duration-500 ease-in-out border-2 border-grey-300 h-12 w-12 flex ietms-center justify-center py-3">
          1
        </div>
        <div className="absolute top-0 text-center mt-16 w-32 text-xs font-medium uppercase">
          description
        </div>
      </div>

      <div className="flex-auto border-t-2 transition duration-500 ease-in-out">
        {/* Display line */}
      </div>
    </div>
  );

  return (
    // centers the items
    <div className="mx-4 p-4 flex justify-between items-center">
      {displaySteps}
    </div>
  );
}

export default Stepper;
