import React from 'react'


const Hero = () => {
  return (
    <div className="text-[#2D8D79]">
      <div className="max-w-[800px] mt-[-96px] w-full h-screen mx-auto flex flex-col justify-center text-left">
        <p className="text-gray font-bold p-2">PLAY YOUR PART TODAY</p>
        <h1 className="text-3xl font-bold md:text-7xl sm:text-6xl text-4xl md:py-6">
          Reduce your carbon footprint for a better tomorrow
        </h1>
      </div>
    </div>
  );
}

export default Hero
