import React from 'react';
// import SiGumtree from "@react-icons/all-files/fa/SiGumTree";
import styled from "styled-components";
import tw from "twin.macro";





const Features = () => {


  const ThreeColumnContainer = styled.div`
    ${tw`mt-10 flex flex-col items-center lg:items-stretch lg:flex-row flex-wrap lg:justify-center max-w-screen-lg mx-auto`}
  `;

  let getIcon;

  switch (icon) {
    case "roller":
      getIcon =
        "https://w7.pngwing.com/pngs/684/631/png-transparent-green-world-illustration-natural-environment-environmental-health-sustainability-environmental-protection-environmentally-friendly-earth-leaf-globe-grass.png";
      break;
    case "monitor":
      getIcon = <MonitorIcon />;
      break;
    case "browser":
      getIcon = <BrowserIcon />;
      break;
    case "printer":
      getIcon = <PrinterIcon />;
      break;
    default:
      getIcon = <RollerIcon />;
      break;
  }


  return (


    <section className="mt-24 mx-auto max-w-screen-xl pb-12 px-4 items-center lg:flex md:px-8 bg-white margin:">
      <div className="space-y-4 flex-1 sm:text-center lg:text-center">
        <h1 class="font-bold text-[#5E9387] text-darken my-3 text-4xl text-center">
          All-In-One <span class="text-gray-700">Carbon Tracker</span>
        </h1>
      </div>
    </section>
  );
}

export default Features
