// export default ClaimReward


function ClaimReward({ open }) {
  const { getRootProps, getInputProps, acceptedFiles, isDragActive } = 
    useDropzone({});



  const files = acceptedFiles.map((file) => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  // console.log(files[0]);

  const imageToUri = require("image-to-uri");
  // console.log(imageToUri(file[0]));



  const postData = (imageURI) => {
    // const apiKey = "AIzaSyC3rJnOOPzFe0snLWmBrgukHmSqFxlimU8";

    console.log("in");

    let body = {
      requests: [
        {
          image: {
            source: {
              imageUri:
                "https://p2d7x8x2.stackpathcdn.com/content/uploads/2016/05/Bill.jpg", //image URL
                // imageURI,
            },
          },
          features: [
            {
              type: "TEXT_DETECTION",
              maxResults: 1,
            },
          ],
        },
      ],
    };

    axios
      .post(
        "https://vision.googleapis.com/v1/images:annotate?key=AIzaSyC3rJnOOPzFe0snLWmBrgukHmSqFxlimU8",
        body
      )
      .then((response) => console.log(response));
  };



  return (
    <div className="container">
      <div {...getRootProps({ className: "dropzone" })}>
        <input className="input-zone" {...getInputProps()} />
        <div className="text-center">
          {isDragActive ? (
            <p className="dropzone-content">Release to drop file here</p>
          ) : (
            <p className="dropzone-content">Drag 'n' drop file here</p>
          )}
          <button
            type="button"
            onClick={open}
            className="btn"
            className="border-2 border-black p-2"
          >
            Click to select file
          </button>
        </div>
      </div>
      <aside>
        <ul>{files}</ul>
      </aside>
      <button className="border-2 border-black p-2" onClick={() => postData()}>
        Post Receipt
      </button>
    </div>
  );
}

export default ClaimReward;
