import React from 'react';
// import SiGumtree from "@react-icons/all-files/fa/SiGumTree";
import styled from "styled-components";
import tw from "twin.macro";


const Wrapper = styled.div`
  width: 100%;
`;
const IconStyle = styled.div`
  @media (max-width: 860px) {
    margin: 0 auto;
  }
`;
const TitleStyle = styled.h2`
  width: 100%;
  max-width: 300px;
  margin: 0 auto;
  padding: 40px 0;
  @media (max-width: 860px) {
    padding: 20px 0;
  }
`;
const SubtitleStyle = styled.p`
  width: 100%;
  max-width: 300px;
  margin: 0 auto;
`;




const Features = ({ image, title, subtitle }) => {

  let getImageUrl;

  switch (image) {
    case "one":
      getImageUrl =
        "https://w7.pngwing.com/pngs/684/631/png-transparent-green-world-illustration-natural-environment-environmental-health-sustainability-environmental-protection-environmentally-friendly-earth-leaf-globe-grass.png";
      break;
    case "two":
      getImageUrl =
        "https://w7.pngwing.com/pngs/684/631/png-transparent-green-world-illustration-natural-environment-environmental-health-sustainability-environmental-protection-environmentally-friendly-earth-leaf-globe-grass.png";
      break;
    case "tree":
      getImageUrl =
        "https://w7.pngwing.com/pngs/684/631/png-transparent-green-world-illustration-natural-environment-environmental-health-sustainability-environmental-protection-environmentally-friendly-earth-leaf-globe-grass.png";
      break;
  }








  return (
    <Wrapper className="flex flexColumn">
      <IconStyle>{getImage}</IconStyle>
      <TitleStyle className="font20 extraBold">{title}</TitleStyle>
      <SubtitleStyle className="font13">{subtitle}</SubtitleStyle>
    </Wrapper>
  );
}

export default Features
